mapper.insert:

	SELECT SEQ_HOBBY_NO.NEXTVAL FROM dual


INSERT INTO 
	tbl_hobby (hobby_no, user_id, hobby_title, main_img,
		hobby_intro, hobby_video, l_cate_no, m_cate_no, 
		time_no, level_no, cost_no 
	)
VALUES (#{hobby_no}, #{user_id}, #{hobby_title}, #{main_img},
	#{hobby_intro}, #{hobby_video, jdbcType=VARCHAR}, #{l_cate_no}, #{m_cate_no},
	#{time_no}, #{level_no}, #{cost_no}
)

update.foreach:
	UPDATE tbl_hobby_material
  	SET is_del = 'Y'
  	WHERE hobby_no = #{item.hobby_no} AND hm_no = #{item.hm_no}

mapper.update:
MERGE INTO tbl_hobby_material A USING (
SELECT #{item.hm_no} as hm_no, #{item.hobby_no} as hobby_no, #{item.material_no} as material_no, #{item.material_detail, jdbcType=VARCHAR} as material_detail, #{item.seq} as seq, #{item.is_del} as is_del FROM DUAL
) B ON (A.HM_NO = B.HM_NO) WHEN MATCHED THEN UPDATE SET A.MATERIAL_NO = B.material_no, A.MATERIAL_DETAIL = B.material_detail, A.SEQ = B.seq, A.is_del = B.is_del WHERE A.HOBBY_NO = B.hobby_no WHEN NOT MATCHED THEN INSERT ( HM_NO, HOBBY_NO, MATERIAL_NO, MATERIAL_DETAIL, SEQ ) VALUES ( seq_hm_no.nextval, B.hobby_no, B.material_no, B.material_detail, B.seq )

update.foreach:
	UPDATE tbl_make_step
  	SET is_del = 'Y'
  	WHERE hobby_no = #{item.hobby_no} AND step_no = #{item.step_no}

mapper.update:
MERGE INTO tbl_make_step A USING (
SELECT #{item.step_no} as step_no, #{item.hobby_no} as hobby_no, #{item.make_step_num} as make_step_num, #{item.make_step_text} as make_step_text, #{item.make_step_img, jdbcType=VARCHAR} as make_step_img, #{item.tip, jdbcType=VARCHAR} as tip, #{item.note, jdbcType=VARCHAR} as note, #{item.link_url, jdbcType=VARCHAR} as link_url, #{item.link_desc, jdbcType=VARCHAR} as link_desc, #{item.is_del} is_del FROM DUAL
) B ON (A.STEP_NO = B.step_no) WHEN MATCHED THEN UPDATE SET A.MAKE_STEP_NUM = B.make_step_num, A.MAKE_STEP_TEXT = B.make_step_text, A.MAKE_STEP_IMG = B.make_step_img, A.TIP = B.tip, A.NOTE = B.note, A.LINK_URL = B.link_url, A.LINK_DESC = link_desc, A.IS_DEL = B.is_del WHERE A.HOBBY_NO = B.hobby_no WHEN NOT MATCHED THEN INSERT ( STEP_NO, HOBBY_NO, MAKE_STEP_NUM, MAKE_STEP_TEXT, MAKE_STEP_IMG, TIP, NOTE, LINK_URL, LINK_DESC ) VALUES ( seq_step_no.nextval, B.hobby_no, B.make_step_num, B.make_step_text, B.make_step_img, B.tip, B.note, B.link_url, B.link_desc )

update.foreach:
	UPDATE tbl_complete_img
  	SET img_name = #{item.img_name, jdbcType=VARCHAR}
  	WHERE hobby_no = #{item.hobby_no} AND num = #{item.num}

mapper.update:
UPDATE tbl_hobby
SET view_cnt = view_cnt + 1
WHERE hobby_no = #{hobby_no}

UPDATE tbl_hobby
SET cmt_cnt = cmt_cnt + #{count}
WHERE hobby_no = #{hobby_no}

UPDATE tbl_hobby
SET mbm_cnt = mbm_cnt + #{count}
WHERE hobby_no = #{hobby_no}
